import React,{useState} from 'react';



const Upload = () => {
    const [file, setFile] = useState("")
    const [filename, setFilename] = useState("")

    const onChange = e =>{
        setFile(e.target.files[0]);
        setFilename(e.target.file[0])
    }


    return (
        <div className="vistaUpload">
            <h1>You can help on this too.</h1>
            <form method="POST" action="/upload" ectype="multipart/form-data">
                <input type="file" name="file" onChange={onChange} />

                <label htmlFor="cusomFiles">
                    {filename}
                </label>

                <input type="submit" value="Upload" name="file" />
            </form>
        </div>
    )
}

export default Upload
